sys_script_include.script:
var integrationGitUtil = Class.create();
integrationGitUtil.prototype = {
    initialize: function(_requestBody,_headerEvent,_action) {
		this.EVENT = _headerEvent;
		this.REQUEST_BODY = _requestBody;
		this.ACTION = _action;
    },

	/**executeIntegration
	* Description: 
	* Parameters: 
	* Returns:
	*/
	executeIntegration: function(){
		this._insertOrUpdateDefects();
	},

	_insertOrUpdateDefects: function() {
		// Check and update defect
		// Else create a new one
		var html_url = this.REQUEST_BODY['issue'].html_url;
		var grRmDefect = new GlideRecord('rm_defect');
		grRmDefect.addEncodedQuery('x_295626_integra_0_github_issue_link='+html_url);
		grRmDefect.query();
		if (grRmDefect.next()) {
			// Update Comments when created
			if (this.EVENT == 'issue_comment') {
				if (this.ACTION == 'created') {
					grRmDefect.comments = 'Comment from GitHub issue - content: '+this.REQUEST_BODY['comment'].body;
					grRmDefect.update();
				}
			}

			// Cancel issue if closed
			if (this.EVENT == 'issues') {
				if (this.ACTION == 'closed') {
					grRmDefect.state = 3;
					grRmDefect.update();
				}
			}

		} else {
			if (this.EVENT == 'issues') {
				if (this.ACTION == 'opened') { 
					grRmDefect.short_description = this.REQUEST_BODY['issue'].title+'';
					grRmDefect.x_295626_integra_0_github_issue_link = html_url;
					grRmDefect.description = this.REQUEST_BODY['issue'].title+'';
					grRmDefect.insert();
				}
			}



		}
	},

    type: 'integrationGitUtil'
};
